def prime_length_substrings(sentence):

    def is_prime(n):
        if n != 1:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    words = sentence.split()
    added_words = set()
    result = []
    for word in words:
        word = ''.join((char for char in word if char.isalpha()))
        if is_prime(len(word)) and word not in added_words:
            result.append(word)
            added_words.add(word)
    return ' '.join(result)

