def count_values(df, col1, col2):
    """"""
    dict_count = {}
    for value1 in df[col1].unique():
        sub_dict = {}
        for value2 in df[df[col1] == value1][col2].unique():
            sub_dict[value2] = df[(df[col1] == value1) & (df[col2] == value2)].shape[0]
