import string

def string_to_dict(s):
    sentences = s.split('')
    dict_sentences = {}
    for sentence in sentences:
        words = sentence.translate(str.maketrans('', '', string.punctuation)).lower().split()
        dict_sentences[sentence] = words
    return dict_sentences

