import numpy as np

def generate_3d_array(l, m, n):
    """"""
    arr = np.empty((l, m, n), dtype=int)
    count = 1
    for k in range(l):
        for j in range(m):
            if j % 2 == 0:
                for i in range(n):
                    arr[k, j, i] = count
                    count += 1
            else:
                for i in range(n - 1, -1, -1):
                    arr[k, j, i] = count
                    count += 1
    return arr
