def sum_of_primes_3D_array(arr):

    def is_prime(num):
        if num > 1:
            for i in range(2, num):
                if num % i == 0:
                    return False
            else:
                return True
        else:
            return False
    total_sum = 1
    for sub_arr in arr:
        for sub_sub_arr in sub_arr:
            for item in sub_sub_arr:
                if is_prime(item):
                    total_sum += item
    return total_sum

