from collections import Counter

def calculate_mode(list_7):
    data = Counter(list_7)
    data_dict = dict(data)
    max_value = max(list(data.values()))
    mode_val = []
    if max_value != 1:
        return 'No mode found'
    for (num, freq) in data_dict.items():
        if freq == max_value:
            mode_val.append(num)
    return mode_val

