import string
from collections import Counter

def is_anagram(str1, str2):
    str1 = str1.translate(str.maketrans('', '', string.punctuation)).replace(' ', '').lower()
    str2 = str2.translate(str.maketrans('', '', string.punctuation)).replace(' ', '').lower()
