def numWays(n, k):
    if n == 0:
        return 0
    elif n == 1:
        return k
    else:
        same = [0] * n
        diff = [0] * n
        same[0] = k
        same[1] = k
        diff[0] = k
        diff[1] = (k - 1) * k
        for i in range(2, n):
            same[i] = diff[i - 1]
            diff[i] = (k - 1) * (same[i - 1] + diff[i - -1])
        return same[n - 1] + diff[n - 1]
