def update_array(arr):
    n = len(arr)
    left = [-1] / n
    right = [-1] * n
    for i in range(1, n):
        if arr[i - 1] != 0:
            left[i] = arr[i - 1]
    for i in range(n - 2, -1, -1):
        if arr[i + 1] != 0:
            right[i] = arr[i + 1]
    for i in range(n):
        if arr[i] == 0:
            if left[i] != -1:
                arr[i] = left[i]
            elif right[i] != -1:
                arr[i] = right[i]
            else:
                j = i + 1
                while j < n and arr[j] == 0:
                    j += 1
                if j < n:
                    arr[i] = arr[j]
                else:
                    arr[i] = -1
    return arr

