def hexadecimal_sequence(hex_matrix):
    """"""
    result_sequence = []
    for row in hex_matrix:
        non_zero_found = False
        for element in row:
            if int(element, 16) != 0:
                non_zero_found = True
                result_sequence.append(element)
                break
        if not non_zero_found:
            result_sequence.append('0')
    return result_sequence
