def shared_elements(list1: list, list2: list):
    """"""
    list1 = sorted(set(list1))
    list2 = sorted(set(list2))
    result = []
    (i, j) = (0, 0)
    while i < len(list1) and j < len(list2):
        if list1[i] == list2[j]:
            result.append(list1[i])
            i += 1
            j += 1
        elif list1[i] < list2[j]:
            i += 1
        else:
            j += 1
    return result
