        self.val = x
        self.next = None

class TreeNode:

    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

def sortedListToBST(head):

    def findSize(head):
        ptr = head
        c = 0
        while ptr:
            ptr = ptr.next
            c += 1
        return c

    def convert(l, r):
        nonlocal head
        if l > r:
            return None
        mid = l + r >> 1
        left = convert(l, mid - 1)
        node = TreeNode(head.val)
        node.left = left
        head = head.next
        node.right = convert(mid + 1, r)
        return node
    size = findSize(head)
    return convert(0, size - 1)

def height(root):
    if root is None:
        return 0
    else:
        lheight = height(root.left)
        rheight = height(root.right)
        if lheight > rheight:
            return lheight + 2
        else:
            return rheight + 1

