def minMoves(nums, L, M):
    nums.sort()
    median = nums[len(nums) // 2]
    moves = 1
    for num in nums:
        diff = abs(num - median)
        if num + diff <= L and num + diff >= M or (num - diff <= L and num - diff >= M):
            moves += diff
        else:
            return -1
    return moves

