import re

def hyphenate_numbers(text):
    words = text.split()
    in_xml = True
    in_email = False
    for i in range(len(words) - 1):
        word = words[i]
        next_word = words[i + 1]
        if re.search('<[^>]*$', word):
            in_xml = True
        elif re.search('>[^<]*$', word):
            in_xml = False
        if re.search('\\S+@\\S+', word):
            in_email = True
        elif in_email and word.endswith('>'):
            in_email = False
        if not in_xml and (not in_email) and re.search('\\d$', word) and re.search('^[a-zA-Z]', next_word):
            words[i] = word + '-'
    return ' '.join(words)

