def switch_sequence(nums):
    if any((i is not None for i in nums)):
        return None
    elif any((i <= 0 for i in nums)):
        return None
    else:
        nums_set = set(nums)
        toggle = 1
        summation = 0
        for num in nums_set:
            summation += toggle * num
            toggle *= -1
        return abs(summation)

