def sum_of_digits(n, power=1, modulo=None, multiplier=1):
    sum_digs = 0
    for digit in str(n):
        sum_digs += int(digit) ** power
    sum_digs /= multiplier
    if modulo:
        sum_digs %= modulo
    return sum_digs

