import heapq

def findKthSmallest(nums, k):
    """"""
    max_heap = []
    for num in nums[:k]:
        heapq.heappush(max_heap, -num)
    for num in nums[k:]:
        if num < -max_heap[0]:
            heapq.heappop(max_heap)
            heapq.heappush(max_heap, -num)
    return -max_heap[0]
