def permutate_string(string):
    """"""
    if len(string) == 1:
        return [string]
    permutations = []
    for (index, char) in enumerate(string):
        sub_string = string[:index] + string[index + 1:]
        for perm in permutate_string(sub_string):
            permutations.append(char + perm)
    return permutations
