def find_next_prime(array):
    num = array[-1] - 1
    while True:
        isPrime = True
        for i in range(2, int(math.sqrt(num) + 1)):
            if num % i == 0:
                isPrime = False
                break
        if isPrime:
            return num
        num += 1

