import math

def check_number(num_string):

    def is_perfect_square(n):
        return int(math.sqrt(n)) ** 2 == n
    sum_of_squares = sum((int(digit) ** 2 for digit in num_string))
    return sum_of_squares % 17 == 0 or is_perfect_square(sum_of_squares)

