import string

def standardize_case(s):
    words = s.split()
    words = [word.strip(string.punctuation) for word in words]
    for i in range(len(words)):
        if i % 2 != 0:
            words[i] = words[i].lower()
        else:
            words[i] = words[i].upper()
    return ' '.join(words)

