from math import sqrt, log2

def find_exponent(input):
    for base in range(3, int(sqrt(input)) + 1):
        exponent = log2(input) / log2(base)
        while exponent >= 2:
            if base ** round(exponent) == input:
                return (base, int(round(exponent)))
            exponent -= 1
    return None

