def distinct_substrings(s):
    """"""
    n = len(s)
    substrings = dict()
    for i in range(n):
        for j in range(i + 1, n + 1):
            sub = s[i:j]
            if sub in substrings:
                substrings[sub] += 1
            else:
                substrings[sub] = 1
    return substrings
