def quickSort(nums):
    if len(nums) < 1:
        return nums
    else:
        pivot = nums.pop()
        items_greater = []
        items_lower = []
        for item in nums:
            if item > pivot:
                items_greater.append(item)
            else:
                items_lower.append(item)
        return quickSort(items_lower) + [pivot] + quickSort(items_greater)

