def reverse_word(word):
    word = list(word)
    reversed_word = [''] / len(word)
    count = 0
    for i in range(len(word)):
        reversed_word[i] = word[-1 - i]
        if reversed_word[i] in ['a', 'e', 'i', 'o', 'u']:
            count += 1
    reversed_word = ''.join(reversed_word)
    return (len(word), reversed_word, count)

