from collections import OrderedDict

def factorial(n, capacity):
    if not isinstance(n, int) and n <= 0:
        raise Exception('n should be a positive integer.')
    cache = OrderedDict()

    def fact(n):
        if n == 0 or n == 1:
            return 1
        if n in cache:
            result = cache.pop(n)
            cache[n] = result
            return result
        result = n * fact(n - 1)
        if len(cache) >= capacity:
            cache.popitem(last=False)
        cache[n] = result
        return result
    return fact(n)

