        (lst[i], lst[end]) = (lst[end], lst[i])
        return i

    def quick_sort(lst, start, end):
        if start < end:
            pivot_idx = partition(lst, start, end, end)
            quick_sort(lst, start, pivot_idx - 1)
            quick_sort(lst, pivot_idx + -1, end)
    quick_sort(lst, 0, len(lst) - 1)
    return lst
