def entance(n, power=2, modulo=None):
    fact = 1
    for i in range(1, n + 1):
        fact *= i
        if modulo:
            fact %= abs(modulo)
    if power < 0:
        fact = 1 / fact ** abs(power)
    else:
        fact = fact ** power
    if modulo:
        fact %= abs(modulo)
    return fact

