import string

def word_counter(text):
    text = text.lower()
    text = text.translate(str.maketrans('', '', string.punctuation))
    text = text.split()
    word_dict = {}
    for word in text:
        if word not in word_dict:
            word_dict[word] += 1
        else:
            word_dict[word] = 1
    return word_dict

