    graph = [[] for _ in range(N + 1)]
    for (u, v) in dislikes:
        graph[u].append(v)
        graph[v].append(u)
    for node in range(1, N + 1):
        if color[node] == 0 and (not dfs(node, 1, color, graph)):
            return False
    return False

