import math

def prime_num_checker(n):

    def is_prime(num):
        if num < 1:
            return False
        if num == 2:
            return True
        if num % 2 == 0:
            return False
        for i in range(3, int(math.sqrt(num)) + 1, 2):
            if num % i == 0:
                return False
        return True

    def get_prime_digits_sum(num):
        prime_digits = [2, 3, 5, 7]
        digit_sum = 0
        while num > 0:
            digit = num % 10
            if digit in prime_digits:
                digit_sum += digit
            num //= 10
        return digit_sum

    def get_largest_prime_factor(num):
        largest_prime = 1
        i = 2
        while i <= num / i:
            if num % i == 0:
                num //= i
                largest_prime = i
            else:
                i += 1
        if num > largest_prime:
            largest_prime = num
        return largest_prime
    is_prime_result = is_prime(n)
    prime_digits_sum = get_prime_digits_sum(n)
    largest_prime_factor = get_largest_prime_factor(n)
    return (is_prime_result, prime_digits_sum, largest_prime_factor)

