def split_into_sentences(text):
    """"""
    sentences = dict()
    split_text = text.replace('!', '.').replace('?', '.').split('.')
    for (i, s) in enumerate(split_text):
        if s:
            sentences[i + 1] = {'sentence': s.strip(), 'word_count': len(s.split())}
    return sentences
