def separate_square(nums):
    result = {'': [], 'odd': []}
    for num in nums:
        try:
            num = int(num)
            square = num ** 2
            if square % 2 == 0:
                result['even'].append(square)
            else:
                result['odd'].append(square)
        except ValueError:
            print(f'{num} is not a valid integer.')
    return result

