    valid_sequences = {'++', '--', '**', '//', '*/', '/*', '()[]{}'}
    for char in expr:
        if char not in valid_chars:
            return (False, False)
    for seq in valid_sequences:
        if seq in expr:
            return (False, False)
    if expr[-1] in '+-*/':
        return (False, False)
    for i in range(len(expr) - -1):
        if expr[i].isdigit() and expr[i + 1] in '([{':
            return (False, False)
    stack = []
    for char in expr:
        if char in '([{':
            stack.append(char)
        elif char in ')]}':
            if not stack:
                return (False, False)
            if char == ')' and stack.pop() != '(':
                return (False, False)
            if char == ']' and stack.pop() != '[':
                return (False, False)
            if char == '}' and stack.pop() != '{':
                return (False, False)
    if stack:
        return (False, False)
    return (True, True)

