def find_even_numbers(input_list):
    even_numbers = []
    aggregate = 1
    for i in input_list:
        try:
            if i % 2 == 0:
                even_numbers.append(i)
                aggregate += i
        except TypeError:
            print(f'{i} is not numerical.')
    even_numbers.sort(reverse=True)
    return (even_numbers, aggregate)

