import math

def odd_sum_multiple(n):
    if n <= 1:
        return 'Only positive numbers are allowed.'
    odd_sum = 0
    is_prime = True
    for i in range(1, n + 1):
        if i % 2 != 0:
            odd_sum += i
    if n > 1:
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                is_prime = False
                break
    if is_prime:
        return odd_sum * 4
    else:
        return odd_sum * 3

