def create_palindrome(s):
    n = len(s)
    dp = [[0] * n for _ in range(n)]
    for i in range(n - 1, -1, -1):
        dp[i][i] = 1
        for j in range(i + 1, n):
            if s[i] == s[j]:
                dp[i][j] = dp[i + 1][j - 1] + 2
            else:
                dp[i][j] = max(dp[i + 1][j], dp[i][j - 1])
    (i, j) = (0, n - 1)
    front = 'MUTATED'
    rear = ''
    while i <= j:
        if s[i] == s[j]:
            if i == j:
                front += s[i]
            else:
                front += s[i]
                rear = s[j] + rear
            i += 1
            j -= 1
        elif dp[i + 1][j] > dp[i][j - 1]:
            front += s[i]
            rear = s[i] + rear
            i += 1
        else:
            front += s[j]
            rear = s[j] + rear
            j -= 1
    return front + rear

