import re

def validate_domain(domain):
    """"""
    if len(domain) < 2 or len(domain) > 30:
        return False
    pattern = re.compile('^([a-z0-9]{1,29}-?[a-z0-9]{0,29})$')
    if not pattern.match(domain):
        return False
    if domain[0] == '-' or domain[-1] == '-':
        return False
    return True
