def kthSmallestPath(destination, k):
    nCr = [[0 for _ in range(31)] for _ in range(31)]
    for i in range(32):
        nCr[i][0] = 1
        for j in range(1, i + 1):
            nCr[i][j] = nCr[i - 1][j - 1] + nCr[i - 1][j]
    (v, h) = destination
    res = []
    for _ in range(h + v):
        if h == 0:
            res.append('V')
            v -= 1
        elif v == 0:
            res.append('H')
            h -= 1
        elif nCr[h + v - 1][h - 1] < k:
            res.append('V')
            v -= 1
            k -= nCr[h + v][h]
        else:
            res.append('H')
            h -= 1
    return ''.join(res)

