def find_val_indices_freq(lst, val):
    """"""
    first_index = -1
    last_index = -1
    freq = 0
    for i in range(len(lst)):
        if lst[i] == val:
            if first_index == -1:
                first_index = i
            last_index = i
            freq += 1
    return (first_index, last_index, freq)
