def sum_vowels(s):
    """"""
    vowels = 'aeiou'
    sum_vowels = 0
    consecutive_count = 0
    for char in s.lower():
        if char.isalpha() and char in vowels:
            if consecutive_count < 2:
                sum_vowels += ord(char) - ord('a') + 1
                consecutive_count += 1
            else:
                consecutive_count = 0
        else:
            consecutive_count = 0
    return sum_vowels
