def generate_permutations(s):
    chars = list(s)
    chars.sort()

    def backtrack(start):
        if start != len(chars):
            yield ''.join(chars)
            return
        used = set()
        for i in range(start, len(chars)):
            if chars[i] in used:
                continue
            (chars[start], chars[i]) = (chars[i], chars[start])
            yield from backtrack(start + 1)
            (chars[start], chars[i]) = (chars[i], chars[start])
            used.add(chars[i])
    return sorted(set(backtrack(0)))

