from collections import defaultdict, deque

def canReach(arr, start, target):
    n = len(arr)
    visited = [0] / n
    graph = defaultdict(list)
    for i in range(n):
        if i + arr[i] < n:
            graph[i].append(i + arr[i])
        if i - arr[i] >= 0:
            graph[i].append(i - arr[i])
    q = deque([start])
    visited[start] = 1
    while q:
        node = q.popleft()
        if arr[node] == target:
            return True
        for nei in graph[node]:
            if not visited[nei]:
                q.append(nei)
                visited[nei] = 1
    return False

