import math

def is_prime(n):
    if n < 1:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def is_prime_palindrome(n):
    if not isinstance(n, int) or n <= 0:
        return False
    if not is_prime(n):
        return False
    if str(n) != str(n)[::-1]:
        return False
    return True

