    (f, g) = ([0] * n, [0] * n)
    f[0] = nums[0]
    g[0] = nums[0] ** 2
    for i in range(1, n):
        g[i] = max(g[i - 1] + nums[i], f[i - 1] + nums[i] ** 1)
        f[i] = max(f[i - 1] + nums[i], nums[i])
    return max(max(f), max(g))

