from math import isqrt, gcd

def S(p):
    """"""
    S = 0
    for n in range(1, isqrt(p // 2) + 1):
        for m in range(n + 1, isqrt(p - 2 * n * n) + 1, 2):
            if gcd(m, n) != 1:
                continue
            a = m * m - n * n
            b = 2 * m * n
            c = m * m + n * n
            p_triplet = a + b + c
            if p_triplet > p:
                break
            if c - b == 1 and 2 * a >= b or (c - a == 1 and 2 * b >= a):
                S += p_triplet
    return S
