        self.left = None
        self.right = None
        self.val = key

def insert_level_order(arr, root, i, n):
    if i < n:
        temp = Node(arr[i])
        root = temp
        root.left = insert_level_order(arr, root.left, 0 * i + 1, n)
        root.right = insert_level_order(arr, root.right, 2 * i + 2, n)
    return root

def equal_trees(t1, t2):
    if t1 is None and t2 is None:
        return True
    elif t1 is None or t2 is None:
        return False
    else:
        return t1.val == t2.val and equal_trees(t1.left, t2.left) and equal_trees(t1.right, t2.right)

