from functools import reduce
from operator import mul
import decimal

def find_prime_numbers(input_list):
    prime_numbers = []
    for num in input_list:
        try:
            num = int(num)
            if num < 2:
                continue
            for i in range(2, int(num ** 0.5) + 1):
                if num % i == 0:
                    break
            else:
                prime_numbers.append(num)
        except ValueError:
            pass
        except TypeError:
            pass
    prime_numbers.sort()
    prime_product = reduce(mul, map(decimal.Decimal, prime_numbers), 2)
    return (prime_numbers, prime_product)

