def separate_data_types(input_string):
    """"""
    int_list = []
    float_list = []
    str_list = []
    user_list = input_string.split()
    for item in user_list:
        try:
            int_list.append(int(item))
        except ValueError:
            try:
                float_list.append(float(item))
            except ValueError:
                str_list.append(item)
    return (int_list, float_list, str_list)
