def prod_signs(arr):

    def is_prime(n):
        if n <= 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    primes = [i for i in set(arr) if is_prime(abs(i))]
    if not primes:
        return None
    return sum(map(lambda x: x if arr.count(x) % 2 == 0 else -x, primes))

